mapper.update:
update crew
set crew_member = concat(crew_member, #{crewmember}), crew_now = crew_now+1
where crew_name = #{crewname}

update crew
set state = 1
where crew_name = #{crewname}

mapper.select:
select * from crew where crew_manager like #{loginId}
and large = 1
and large = 2

select * from crew where crew_manager not like #{loginId} and crew_member like #{loginId}
and large = 1
and large = 2
