mapper.update:
update products
set readcount = readcount + 1
where products_num = #{num}

update products
set state = 1
where products_num = #{productsnum}

update products
set kind=#{kind}, name=#{name}, price=#{price}, info=#{info}, image=#{image}
where products_num = #{productsnum}

update products
set state = 0
where products_num = #{productsnum}

mapper.select:
 		select products_num, kind, name, info, price, image, seller, inputdate, state, readcount,
 		(select count(*) from products_comments where idx = products_num) as comments
		from products

			where kind = 'a'

 				and name like #{keyword}


 				and seller = #{keyword}


 				and state = 0


 				and state = 1



			where kind = 'b'

 				and name like #{keyword}


 				and seller = #{keyword}


 				and state = 0


 				and state = 1



			where kind = 'c'

 				and name like #{keyword}


 				and seller = #{keyword}


 				and state = 0


 				and state = 1




	 			where name like #{keyword}


	 			where seller = #{keyword}


	 			where state = 0


	 			where state = 1

select products_num, kind, name, info, price, image, seller, inputdate, state, readcount, (select count(*) from products_comments where idx = products_num) as comments from products where seller = #{loginId}
and state = 0
and state = 1
and name like #{keyword}

 		select count(*) from products
 		where seller = #{loginId}

			and state = 0


