mapper.select:
SELECT stamp_num, mountain_num, users_id, stamp_image, stamp_apply, stamp_reject, mountain_exist FROM stamp
WHERE users_id like #{keyword}
ORDER BY stamp_num desc

mapper.update:
UPDATE stamp SET stamp_apply = #{stampapply}
, stamp_reject = null
WHERE stamp_num = #{stampnum}

UPDATE
	stamp
SET
	stamp_reject = #{stampreject}
WHERE
	stamp_num = #{stampnum}
