mapper.update:
update supporters 
set readcount = readcount+1 
where num=#{num}

mapper.select:
select num, writer, supporters_name,deadline, target, limit, applicant, TO_CHAR(start_date, 'YYYY-MM-DD')start_date, TO_CHAR(end_date, 'YYYY-MM-DD')end_date,content, reg_date, readcount,
CASE WHEN deadline < TO_CHAR(sysdate,'YYYY-MM-DD') then 2 WHEN applicant >= "LIMIT" then 1 ELSE 0 END AS state
from supporters where num = #{num}

mapper.update:
update supporters 
set supporters_name=#{supportersname}, deadline=#{deadline}, target=#{target}, 
limit=#{limit}, applicant=#{applicant}, start_date=#{startdate}, end_date=#{enddate}, content=#{content}
where num = #{num}

update supporters_apply 
set state = 1 
where join_num = #{num}

mapper.select:
select s.num, s.supporters_name, s.deadline, s.target, s.limit, s.applicant, TO_CHAR(s.start_date, 'YYYY-MM-DD')start_date, TO_CHAR(s.end_date, 'YYYY-MM-DD')end_date,
CASE WHEN s.deadline < TO_CHAR(sysdate,'YYYY-MM-DD') then 2 WHEN s.applicant >= s.limit then 1 ELSE 0 END AS state
from supporters s inner join supporters_apply su on s.num = su.join_num where su.state = 0 and su.applicant_id like #{keyword}
