mapper.update:
update supporters 
set readcount = readcount+1 
where num=#{num}

mapper.select:
select num, writer, supporters_name,deadline, target, limit, applicant, TO_CHAR(start_date, 'YYYY-MM-DD')start_date, TO_CHAR(end_date, 'YYYY-MM-DD')end_date,content, reg_date, readcount,
CASE WHEN deadline < TO_CHAR(sysdate,'YYYY-MM-DD') then 2 WHEN applicant >= "LIMIT" then 1 ELSE 0 END AS state
from supporters where num = #{num}

mapper.update:
update supporters 
set supporters_name=#{supportersname}, deadline=#{deadline}, target=#{target}, 
limit=#{limit}, applicant=#{applicant}, start_date=#{startdate}, end_date=#{enddate}, content=#{content}
where num = #{num}
